import React, { useState, useEffect } from "react";
import "./Dashboard.css";

// Simulate API call (replace with a real API as needed)
const fetchData = () =>
  new Promise((resolve) =>
    setTimeout(
      () =>
        resolve([
          { id: 1, name: "Alice", role: "Researcher", score: 85 },
          { id: 2, name: "Bob", role: "Data Analyst", score: 90 },
          { id: 3, name: "Charlie", role: "Developer", score: 78 },
          { id: 4, name: "Dana", role: "Researcher", score: 92 },
        ]),
      1000
    )
  );

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [sortKey, setSortKey] = useState("id");
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchData().then((result) => setData(result));
  }, []);

  const sortData = (key) => {
    setSortKey(key);
    const sortedData = [...data].sort((a, b) =>
      a[key] > b[key] ? 1 : a[key] < b[key] ? -1 : 0
    );
    setData(sortedData);
  };

  const filteredData = data.filter(
    (item) =>
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.role.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="dashboard-container">
      <h1>Dynamic Dashboard</h1>
      <div className="controls">
        <input
          type="text"
          placeholder="Search by name or role"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="sort-buttons">
          <button onClick={() => sortData("id")}>Sort by ID</button>
          <button onClick={() => sortData("name")}>Sort by Name</button>
          <button onClick={() => sortData("score")}>Sort by Score</button>
        </div>
      </div>
      <table className="data-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Role</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.length > 0 ? (
            filteredData.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.role}</td>
                <td>{item.score}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No data found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
